import React, { useState } from "react";
import axios from "axios";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import NavbarDashboard from "../components/NavbarDashboard";
import Footer from "../components/Footer";

const CitizenDashboard = () => {
  const [formData, setFormData] = useState({
    title: "",
    description: "",
    category: "",
  });
  const [loading, setLoading] = useState(false);

  // handle input change
  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  // handle submit (sending issue to backend)
  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      await axios.post("http://localhost:5000/api/issues", formData);
      toast.success("✅ Issue reported successfully!");
      setFormData({ title: "", description: "", category: "" }); // clear form
    } catch (err) {
      console.error(err);
      toast.error("❌ Failed to submit issue");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="flex flex-col items-center min-h-screen bg-gray-50">
      {/* Navbar fixed at top */}
      <div className="fixed top-0 left-0 w-full z-50">
        <NavbarDashboard />
      </div>

      {/* Toast Notifications */}
      <ToastContainer
        position="bottom-center"
        autoClose={4000}
        hideProgressBar
        closeOnClick
        pauseOnHover
        theme="light"
      />

      {/* Content Section */}
      <div className="mt-24 w-full max-w-lg bg-white p-6 rounded-2xl shadow-lg">
        <h2 className="text-2xl font-bold mb-6 text-center">Report an Issue</h2>
        <form onSubmit={handleSubmit} className="space-y-4">
          {/* Title */}
          <input
            type="text"
            name="title"
            value={formData.title}
            onChange={handleChange}
            placeholder="Enter issue title"
            className="w-full border p-2 rounded-lg"
            required
          />

          {/* Category */}
          <input
            type="text"
            name="category"
            value={formData.category}
            onChange={handleChange}
            placeholder="Enter issue category"
            className="w-full border p-2 rounded-lg"
            required
          />

          {/* Description */}
          <textarea
            name="description"
            value={formData.description}
            onChange={handleChange}
            placeholder="Describe the issue"
            className="w-full border p-2 rounded-lg h-28"
            required
          />

          {/* Submit Button */}
          <button
            type="submit"
            className="w-full bg-blue-600 hover:bg-blue-700 text-white font-semibold py-2 px-4 rounded-lg"
            disabled={loading}
          >
            {loading ? "Submitting..." : "Submit Issue"}
          </button>
        </form>
      </div>

      {/* Example Images Section */}
      <div className="mt-10 w-full max-w-4xl">
        <h3 className="text-xl font-semibold mb-4 text-center">
          Types of Issues You Can Report
        </h3>
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-10">
          {/* Replace image src with actual issue-related images */}
          <div className="bg-white shadow rounded-lg overflow-hidden">
            <img
              src="https://via.placeholder.com/300x200?text=Road+Damage"
              alt="Road Damage"
              className="w-full h-40 object-cover"
            />
            <p className="p-2 text-center text-sm font-medium">Road Damage</p>
          </div>

          <div className="bg-white shadow rounded-lg overflow-hidden">
            <img
              src="https://via.placeholder.com/300x200?text=Traffic+Signal+Issue"
              alt="Traffic Signal Issue"
              className="w-full h-40 object-cover"
            />
            <p className="p-2 text-center text-sm font-medium">Traffic Signal Issue</p>
          </div>

          <div className="bg-white shadow rounded-lg overflow-hidden">
            <img
              src="https://via.placeholder.com/300x200?text=Garbage"
              alt="Garbage"
              className="w-full h-40 object-cover"
            />
            <p className="p-2 text-center text-sm font-medium">Garbage</p>
          </div>

          <div className="bg-white shadow rounded-lg overflow-hidden">
            <img
              src="https://via.placeholder.com/300x200?text=Illegal+Parking"
              alt="Illegal Parking"
              className="w-full h-40 object-cover"
            />
            <p className="p-2 text-center text-sm font-medium">Illegal Parking</p>
          </div>

          <div className="bg-white shadow rounded-lg overflow-hidden">
            <img
              src="https://via.placeholder.com/300x200?text=Streetlight+Not+Working"
              alt="Streetlight"
              className="w-full h-40 object-cover"
            />
            <p className="p-2 text-center text-sm font-medium">Streetlight Not Working</p>
          </div>

          <div className="bg-white shadow rounded-lg overflow-hidden">
            <img
              src="https://via.placeholder.com/300x200?text=Other+Issues"
              alt="Other Issues"
              className="w-full h-40 object-cover"
            />
            <p className="p-2 text-center text-sm font-medium">Other Issues</p>
          </div>
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default CitizenDashboard;
