// src/pages/CitizenDashboard.js
import React, { useEffect, useState } from "react";
import axios from "axios";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import NavbarDashboard from "../components/NavbarDashboard";
import Footer from "../components/Footer";
import { Link } from "react-router-dom";

const CitizenDashboard = () => {
  const [complaints, setComplaints] = useState([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const fetchComplaints = async () => {
      setLoading(true);
      try {
        const userId = localStorage.getItem("userId");
        if (!userId) throw new Error("User not logged in");

        const res = await axios.get(
          `http://localhost:5000/api/complaints/user/${userId}`
        );

        setComplaints(res.data);
      } catch (err) {
        console.error("Error fetching complaints:", err);
        toast.error("‚ùå Failed to fetch complaints");
      } finally {
        setLoading(false);
      }
    };

    fetchComplaints();
  }, []);

  return (
    <div className="flex flex-col items-center min-h-screen bg-gray-50">
      {/* Navbar fixed at top */}
      <div className="fixed top-0 left-0 w-full z-50">
        <NavbarDashboard />
      </div>

      {/* Toast Notifications */}
      <ToastContainer
        position="bottom-center"
        autoClose={4000}
        hideProgressBar
        closeOnClick
        pauseOnHover
        theme="light"
      />

      {/* Complaints List */}
      <div className="mt-24 w-full max-w-3xl bg-white p-6 rounded-2xl shadow-lg">
        <h2 className="text-2xl font-bold mb-6 text-center">
          Complaints Reported By You
        </h2>

        {loading ? (
          <p className="text-center text-gray-500">Loading...</p>
        ) : complaints.length === 0 ? (
          <p className="text-center text-gray-500">
            No complaints reported yet.
          </p>
        ) : (
          <div className="space-y-4">
            {complaints.map((complaint) => (
              <div
                key={complaint._id}
                className="border rounded-lg p-4 shadow-sm bg-gradient-to-r from-blue-50 to-cyan-50"
              >
                <div className="flex justify-between items-center mb-2">
                  <p className="text-sm text-gray-600">
                    Reported Date:{" "}
                    {new Date(complaint.createdAt).toLocaleDateString()}
                  </p>
                  <Link
                    to={`/complaints/${complaint._id}`}
                    className="text-blue-600 hover:underline text-sm font-medium"
                  >
                    Detailed View
                  </Link>
                </div>
                <p className="font-semibold">{complaint.reason}</p>
                <p className="text-sm text-gray-600">üìç {complaint.location}</p>
                {complaint.moreInfo && (
                  <p className="text-sm text-gray-500 mt-1">{complaint.moreInfo}</p>
                )}
                {complaint.fileUrl && (
                  <a
                    href={complaint.fileUrl}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="text-blue-600 text-sm mt-1 block hover:underline"
                  >
                    View Uploaded File
                  </a>
                )}
                <p
                  className={`mt-2 font-semibold ${
                    complaint.status === "PENDING"
                      ? "text-yellow-600"
                      : complaint.status === "SOLVED"
                      ? "text-green-600"
                      : "text-red-600"
                  }`}
                >
                  Status: {complaint.status}
                </p>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Example Images Section */}
      <div className="mt-10 w-full max-w-4xl">
        <h3 className="text-xl font-semibold mb-4 text-center">
          Types of Issues You Can Report
        </h3>
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-10">
          {[
            { img: "/roaddamage.jpeg", title: "Road Damage" },
            { img: "/trafficissue.jpeg", title: "Traffic Signal Issue" },
            { img: "/garbage.jpg", title: "Garbage on Roads" },
            { img: "/illegalparking.jpg", title: "Illegal Parking" },
            { img: "/streetlight.jpeg", title: "Streetlight Not Working" },
            { img: "/waterleak.webp", title: "Other Issues" },
          ].map((issue, idx) => (
            <Link to="/report-issue" key={idx}>
              <div className="bg-white shadow rounded-lg overflow-hidden transform transition duration-300 hover:scale-105 hover:shadow-lg">
                <img
                  src={issue.img}
                  alt={issue.title}
                  className="w-full h-40 object-cover transition duration-300 hover:opacity-90"
                />
                <p className="p-2 text-center text-sm font-medium transition-colors duration-300 hover:text-blue-600">
                  {issue.title}
                </p>
              </div>
            </Link>
          ))}
        </div>
      </div>

      <Footer />
    </div>
  );
};

export default CitizenDashboard;
