import React, { useState } from "react";
import axios from "axios";
import NavbarDashboard from "../components/NavbarDashboard";

const CitizenDashboard = () => {
  const [formData, setFormData] = useState({
    title: "",
    description: "",
    category: "",
  });

  // handle input change
  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  // handle submit (sending issue to backend)
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/api/issues", formData);
      alert("Issue reported successfully!");
      setFormData({ title: "", description: "", category: "" }); // clear form
    } catch (err) {
      console.error(err);
      alert("Failed to submit issue");
    }
  };

  return (
    <div className="flex flex-col items-center min-h-screen bg-gray-50">
      {/* Navbar fixed at top */}
      <div className="fixed top-0 left-0 w-full z-50">
        <NavbarDashboard />
      </div>

      {/* Content Section */}
      <div className="mt-24 w-full max-w-lg bg-white p-6 rounded-2xl shadow-lg">
        <h2 className="text-2xl font-bold mb-4 text-center">Report an Issue</h2>
        <form onSubmit={handleSubmit} className="space-y-4">
          <input
            type="text"
            name="title"
            value={formData.title}
            onChange={handleChange}
            placeholder="Enter issue title"
            className="w-full border p-2 rounded-lg"
          />
          <textarea
            name="description"
            value={formData.description}
            onChange={handleChange}
            placeholder="Describe the issue"
            className="w-full border p-2 rounded-lg"
          />
          <select
            name="category"
            value={formData.category}
            onChange={handleChange}
            className="w-full border p-2 rounded-lg"
          >
            <option value="">Select Category</option>
            <option value="Road">Road</option>
            <option value="Water">Water</option>
            <option value="Electricity">Electricity</option>
          </select>
          <button
            type="submit"
            className="w-full bg-blue-600 text-white py-2 rounded-lg hover:bg-blue-700"
          >
            Submit Issue
          </button>
        </form>
      </div>
    </div>
  );
};

export default CitizenDashboard;
